package edu;

public class Test27 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0; i<10; i++) {
			System.out.println(i);
		}
		
		int j = 0;
		while(j<10) {
			System.out.println(j);
			j++;
		}
		
		j = 0;
		do {
			System.out.println(j);
			j++;
		} while(j<10);
	}

}
